import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: HomeScreen(),
    );
  }
}

class HomeScreen extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('MyApp'),
        backgroundColor: Color.fromARGB(255, 247, 244, 243),
        bottom: TabBar(
          tabs: [
            Tab(text: 'BERITA TERBARU'),
            Tab(text: 'PERTANDINGAN HARI INI'),
          ],
        ),
      ),
      body: ListView(
        children: [
          beritaItem(
            'Costa Mendekat Ke Palmeiras',
            'New York   March 12, 2023',
            'data:image/jpeg;base64,', // Ganti dengan URL gambar asli
            Color.fromARGB(255, 216, 196, 84),
          ),
          beritaItem(
            'Olahraga penting untuk kesehatan',
            'Indonesia  Feb 13, 2021',
            'data:image/jpeg;base64,',
            Color.fromARGB(255, 193, 187, 83),
          ),
          beritaItem(
            '2 Atlet Bulutangkis berhasil membawa pulang medali',
            'Indonesia  Mei 20, 2020',
            'data:image/jpeg;base64,',
            Color.fromARGB(255, 193, 187, 83),
          ),
        ],
      ),
    );
  }

  Widget beritaItem(
      String title, String subtitle, String imageUrl, Color color) {
    return Container(
      margin: EdgeInsets.all(8.0),
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(8.0),
        boxShadow: [
          BoxShadow(
            color: Colors.black12,
            blurRadius: 4.0,
            offset: Offset(0, 2),
          ),
        ],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Image.network(imageUrl),
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: Text(
              title,
              style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
            ),
          ),
          Container(
            color: color,
            padding: EdgeInsets.symmetric(horizontal: 8.0, vertical: 4.0),
            child: Text(
              subtitle,
              style: TextStyle(color: Colors.white),
            ),
          ),
        ],
      ),
    );
  }
}
